import React from 'react';
import CardList from './components/card-list/card-list.component'
import './App.css';

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      monsters: [],
      serachField : ''
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(resp => resp.json())
      .then(users => this.setState({ monsters: users }))
  }
  render() {
    const {monsters,serachField} = this.state;
    const filteredMonsters = monsters.filter(monster => monster.name.toLowerCase().includes(serachField.toLowerCase()))
    return (
      <div className="App">
        <input type="search" placeholder="Search monster" onChange={e => this.setState({serachField : e.target.value})}/>
        <CardList monsters={filteredMonsters} />
      </div>);
  }

}

export default App;
